import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchData, fetchDetails } from "../../../utils/utils";
import { setTvSeason } from "../../../slices/TvShowSlice";
import TvHeader from "../TvHeader";

const SeasonDetails = () => {
  const dispatch = useDispatch();
  const { id, season_number, episode_count } = useParams();

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `tv/${id}/season/${season_number}?language=en-US`
      );

      dispatch(setTvSeason(result));
    }

    fetchDataFunction();
  }, [id, season_number]);

  const seasonsDetailed = useSelector((state) => state.series.tvSeason.data);
  const tvSeasons = useSelector((state) => state.series.tvDetail.data);

  return (
    <div className="flex flex-col gap-5 pl-10 pr-10">
      <TvHeader />
      <div className=" flex flex-row gap-4">
        <img
          src={`https://image.tmdb.org/t/p/original${seasonsDetailed.poster_path}`}
          alt={seasonsDetailed.name}
          className="w-auto h-[200px]"
        />
        <span>
          Air Date: {seasonsDetailed.air_date.split("-")[2]}.
          {seasonsDetailed.air_date.split("-")[1]}.
          {seasonsDetailed.air_date.split("-")[0]}
        </span>
        <span>Episode count: {tvSeasons.episode_count}</span>
      </div>
    </div>
  );
};

export default SeasonDetails;
