import React, { useEffect } from "react";
import { setTvKeyword } from "../../slices/TvShowSlice";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const Keywords = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`tv/${id}/keywords`);
      dispatch(setTvKeyword(result));
    }

    fetchDataFunction();
  }, [id]);

  const tvKeywords = useSelector((state) => state.series.tvKeyword.data);

  return (
    <div className="flex flex-col items-start gap-3">
      <span className="font-serif text-xl italic">Keywords</span>

      {tvKeywords && (
        <div className="grid grid-cols-4 items-start gap-2">
          {tvKeywords.results?.map((item, index) => {
            return (
              <ul key={index} className="flex items-center pr-5">
                <li className="border rounded-xl p-1 text-sm">{item.name}</li>
              </ul>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default Keywords;
