import React from "react";
import { useSelector } from "react-redux";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";
import { Mousewheel, Pagination, Scrollbar } from "swiper/modules";
import { Link } from "react-router-dom";

const SwiperSeazons = () => {
  const tvSeasons = useSelector((state) => state.series.tvDetail.data);
  const seasonsDetailed = useSelector((state) => state.series.tvSeason.data);
  return (
    <Swiper
      direction={"horizontal"}
      slidesPerView={6}
      spaceBetween={0}
      mousewheel={true}
      modules={[Mousewheel, Pagination, Scrollbar]}
      className="mt-5 "
    >
      {tvSeasons.seasons?.slice(0, 8).map((item, index) => {
        return (
          <SwiperSlide key={index} className="p-2 ">
            <Link to={`/tvshow/${id}/seasons/${number}`}>
              <div className="h-[70%] flex gap-2 justify-center">
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                  className="rounded-xl"
                />
              </div>
              <div className="h-[30%] font-serif text-gray-400 flex justify-center flex-col mt-2 gap-1 p-1">
                <span className="text-lg">{item.name}</span>
                <span className="text-sm text-gray-500">
                  {item.episode_count} Episodes
                </span>
              </div>
            </Link>
          </SwiperSlide>
        );
      })}
    </Swiper>
  );
};

export default SwiperSeazons;
