import React from "react";
import { Link } from "react-router-dom";
import Movies from "../movies/Movies";

const SearchBar = ({
  data,
  movieResult = false,
  serieResult = false,
  actorResult = false,
}) => {
  return (
    <div className="absolute top-14 p-2 bg-gray-500 w-full rounded-xl flex flex-col z-[2] overflow-y-auto h-[200px]">
      {data?.map((item, index) => {
        return (
          <div key={index}>
            {movieResult && (
              // <Link
              //   to={`/movie/${item.id}`}
              //   className="flex flex-row gap-10 mb-1 border-b border-black "
              // >
              //   <img
              //     src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
              //     alt={item.title}
              //     className="w-[36px] rounded "
              //   />
              //   <div className="flex flex-col justify-between">
              //     <span className="text-sm font-serif text-black font-bold italic">
              //       {item.title}
              //     </span>
              //     <span className="text-sm font-serif text-black font-bold italic">
              //       {item.release_date?.split("-")[0]}
              //     </span>
              //   </div>
              // </Link>
              <Movies />
            )}
            {serieResult && (
              <Link
                to={`/tvshow/${item.id}`}
                className="flex flex-row gap-10 mb-1 border-b border-black "
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                  className="w-[36px] rounded "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.name}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.first_air_date}
                  </span>
                </div>
              </Link>
            )}
            {actorResult && (
              <Link
                to={`/actor/${item.id}`}
                className="flex flex-row gap-10 mb-1 border-b border-black "
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                  alt={item.name}
                  className="w-[36px] rounded "
                />
                <div className="flex flex-col justify-between">
                  <span className="text-sm font-serif text-black font-bold italic">
                    {item.name}
                  </span>
                  <span className="text-sm font-serif text-black font-bold italic">
                    Known for {item.known_for_department}
                  </span>
                </div>
              </Link>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default SearchBar;
