import React, { useState } from "react";
import { fetchData, fetchDetails } from "../../utils/utils";

const SearchBar = () => {
  const [search, setSearch] = useState("");
  const [movieResult, setMovieResult] = useState([]);
  const [seriesResult, setSeriesResult] = useState([]);
  const [actorsResult, setActorsResult] = useState([]);

  const handlerSearch = async (e) => {
    if (e.key === "Enter") {
      const res = await fetchData(
        `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
      );
      // fetchData(
      //   `search/tv?query=${search}&include_adult=false&language=en-US&page=1`
      // );
      // fetchData(
      //   `search/person?query=${search}&include_adult=false&language=en-US&page=1`
      // );
      setMovieResult(res);
      // setSeriesResult(res);
      // setActorsResult(res);
    }
  };

  return (
    <div className="relative w-[380px]">
      <input
        className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl"
        type="text"
        // size="50"
        placeholder="Search Movie / Tv Show / Actors "
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        onKeyDown={handlerSearch}
      />
      <div className="absolute z-[2] w-[100%] h-[400px] overflow-y-auto bg-slate-500 rounded-xl">
        {movieResult?.map((item, index) => {
          return (
            <div className="flex flex-row " key={index}>
              <img
                src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                alt={movieResult.title}
                className="w-[70px]"
              />
              <span>{item.title}</span>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default SearchBar;
