import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchData } from "../utils/utils";
import { getCategoryData } from "../slices/AppSlice";

const NavBar = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    (async () => {
      await fetchData("titles/utils/titleTypes").then((res) =>
        dispatch(getCategoryData(res))
      );
    })();
  }, []);
  const categoryDataRedux = useSelector((state) => state.app.categoryData.data);
  console.log(categoryDataRedux);
  return (
    <nav className="flex flex-row bg-black h-12 text-white text-xs gap-6 items-center pl-10 pr-10">
      <div className="flex items-center cursor-pointer h-12">
        <img className="h-12" src={require(`../imdb.png`)} alt="" />
      </div>
      <div className="flex flex-row items-center justify-center gap-1.5 h-12">
        <svg
          className="cursor-pointer"
          fill="#FFFFFF"
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 50 50"
          width="30px"
          height="30px"
        >
          <path d="M 5 8 A 2.0002 2.0002 0 1 0 5 12 L 45 12 A 2.0002 2.0002 0 1 0 45 8 L 5 8 z M 5 23 A 2.0002 2.0002 0 1 0 5 27 L 45 27 A 2.0002 2.0002 0 1 0 45 23 L 5 23 z M 5 38 A 2.0002 2.0002 0 1 0 5 42 L 45 42 A 2.0002 2.0002 0 1 0 45 38 L 5 38 z" />
        </svg>
        <span className="flex items-center text-sm">Menu</span>
      </div>
      {/* {categoryDataRedux?.map((item, index) => {
        return <div key={index}>{item}</div>;
      })} */}
      <div className="flex flex-row items-center">
        <form>
          <label className="flex flex-row items-center bg-white">
            <span>All</span>
            <svg
              width="24px"
              height="24px"
              viewBox="0 0 24 24"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              <path
                d="M17.9188 8.17969H11.6888H6.07877C5.11877 8.17969 4.63877 9.33969 5.31877 10.0197L10.4988 15.1997C11.3288 16.0297 12.6788 16.0297 13.5088 15.1997L15.4788 13.2297L18.6888 10.0197C19.3588 9.33969 18.8788 8.17969 17.9188 8.17969Z"
                fill="#292D32"
              />
            </svg>
          </label>
        </form>
        <input
          className="h-8 flex items-center rounded-l text-start p-2"
          type="search"
          size="45"
          placeholder="Search movie ..."
        />
        <button>
          <svg
            className="bg-white cursor-pointer rounded-r"
            xmlns="http://www.w3.org/2000/svg"
            viewBox="0 0 50 50"
            width="32px"
            height="32px"
          >
            <path d="M 21 3 C 11.601563 3 4 10.601563 4 20 C 4 29.398438 11.601563 37 21 37 C 24.355469 37 27.460938 36.015625 30.09375 34.34375 L 42.375 46.625 L 46.625 42.375 L 34.5 30.28125 C 36.679688 27.421875 38 23.878906 38 20 C 38 10.601563 30.398438 3 21 3 Z M 21 7 C 28.199219 7 34 12.800781 34 20 C 34 27.199219 28.199219 33 21 33 C 13.800781 33 8 27.199219 8 20 C 8 12.800781 13.800781 7 21 7 Z" />
          </svg>
        </button>
      </div>
    </nav>
  );
};

export default NavBar;
