import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { tvShows } from "../../slices/AppSlice";
import { loadingMore } from "../../slices/AppSlice";

const TvShows = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("/tv/popular?language=en-US&page=1");
      dispatch(tvShows(res));
    }
    fetchDataFunction();
  }, []);
  const tvShowsList = useSelector((state) => state.app.tvShow.data);
  const nextPage = useSelector((state) => state.app.loadMore.page);

  const handleLoadMore = async () => {
    dispatch(loadingMore());

    const res = await fetchData(
      `/movie/now_playing?language=en-US&page=${nextPage}`
    );
    dispatch(tvShows([...tvShowsList, ...res]));
  };

  return (
    <div className="pl-10 pr-10 flex flex-col gap-2">
      <span className="text-2xl font-serif">TV Shows</span>
      <div className="flex flex-row">
        <div className="w-[30%]">Filtrare</div>
        <div className="grid grid-cols-4 w-[70%] gap-5">
          {tvShowsList?.map((item, index) => {
            return (
              <div key={index} className="flex flex-col rounded-xl bg-gray-700">
                <img
                  className="rounded-xl flex items-center"
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                />

                <div className="p-2 text-xl h-24  font-serif">{item.name}</div>
                <div className="text-xs text-gray-400 p-2 font-serif h-18 items-center">
                  {item.first_air_date}
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <button onClick={handleLoadMore}>
        <span className="bg-yellow-500 rounded-xl p-2 text-black font-serif font-bold">
          Load More
        </span>
      </button>
    </div>
  );
};

export default TvShows;
