import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { nowPlayingMovies } from "../../slices/AppSlice";
import { fetchData } from "../../utils/utils";

const NowPlaying = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("/movie/now_playing?language=en-US");
      dispatch(nowPlayingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const nowPlayingMovie = useSelector((state) => state.app.nowPlaying.data);

  return (
    <div>
      <span className="text-2xl font-sans">Now Playing</span>
      <div className="flex flex-row">
        <div className="w-[30%]">Filtrare</div>
        <div className="grid grid-cols-4 w-[70%] gap-5">
          {nowPlayingMovie?.map((item, index) => {
            return (
              <div key={index} className="flex flex-col rounded-xl bg-gray-700">
                <img
                  className="rounded-xl flex items-center"
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                />

                <div className="p-2 text-xl h-24  font-serif">{item.title}</div>
                <div className="text-xs text-gray-400 p-2 font-serif h-18 items-center">
                  {item.release_date}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};
export default NowPlaying;
