import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <>
      {movieDetails && (
        <div>
          <div className="h-[500px]">
            <img
              src={`https://image.tmdb.org/t/p/original${movieDetails.backdrop_path}`}
              alt={movieDetails.title}
              className="w-screen h-auto"
            />
          </div>
          <div>{movieDetails.overview}</div>
        </div>
      )}
    </>
  );
};

export default MoviesDetails;
