import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { upComingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Scrollbar, A11y } from "swiper/modules";
// Import Swiper styles
import "swiper/css";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("titles/x/upcoming");
      dispatch(upComingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);

  return (
    <>
      <Swiper
        modules={[Navigation, Pagination, Scrollbar, A11y]}
        spaceBetween={100}
        slidesPerView={2}
        navigation
        pagination={{ dynamicBullets: true }}
        scrollbar={{ draggable: true }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log("slide change")}
      >
        {upComingMovie?.map((item, index) => {
          return (
            <SwiperSlide>
              <div
                key={index}
                className="border-2 bg-black rounded-xl p-10 w-[500px] "
              >
                <img
                  src={item.primaryImage.url}
                  alt={""}
                  className="w-[400px] h-auto aspect-auto"
                />
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
