import { createSlice } from "@reduxjs/toolkit";
import { act } from "react-dom/test-utils";

const initialState = {
  modal: {
    isOpen: false,
    // modalType:''
  },
  appData: {
    data: null,
  },

  trendingMovie: {
    data: null,
  },
  trendingTv: {
    data: null,
  },
  nowPlaying: {
    data: [],
  },
  popularMovie: {
    data: [],
  },
  topRatedMovie: {
    data: null,
  },
  upComing: {
    data: null,
  },
};

export const AppSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    openModal: (state, action) => {
      console.log("deschid modala");
      state.modal.isOpen = true;
    },
    closeModal: (state, action) => {
      console.log("inchid modala");
      state.modal.isOpen = false;
    },
    trendingMovies: (state, action) => {
      state.trendingMovie.data = action.payload;
    },
    trendingTvShows: (state, action) => {
      state.trendingTv.data = action.payload;
    },

    nowPlayingMovies: (state, action) => {
      state.nowPlaying.data = action.payload;
    },

    popularMovies: (state, action) => {
      state.popularMovie.data = action.payload;
    },

    topRatedMovies: (state, action) => {
      state.topRatedMovie.data = action.payload;
    },
    upComingMovies: (state, action) => {
      state.upComing.data = action.payload;
    },
  },
});

export const {
  openModal,
  closeModal,
  getCategoryData,
  getAppData,
  trendingMovies,
  trendingTvShows,
  nowPlayingMovies,
  popularMovies,
  topRatedMovies,
  upComingMovies,
} = AppSlice.actions;

export default AppSlice.reducer;
