import React, { useEffect } from "react";
import { setActor } from "../../slices/ActorSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchData } from "../../utils/utils";
import { loadingMore } from "../../slices/AppSlice";

const Actors = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("/person/popular?language=en-US&page=1");
      dispatch(actors(res));
    }
    fetchDataFunction();
  }, []);
  const actorsList = useSelector((state) => state.app.actor.data);
  const nextPage = useSelector((state) => state.app.loadMore.page);

  const handleLoadMore = async () => {
    dispatch(loadingMore());

    const res = await fetchData(
      `/person/popular?language=en-US&page=${nextPage}`
    );
    dispatch(actors([...actorsList, ...res]));
  };

  return (
    <div className="pl-10 pr-10 flex flex-col gap-3">
      <span className="text-2xl font-serif">Actors</span>
      <div className="grid grid-cols-4 gap-5 pb-3">
        {actorsList?.map((item, index) => {
          return (
            <div key={index} className="flex flex-col rounded-xl bg-gray-700">
              <img
                className="rounded-xl flex items-center"
                src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                alt={item.name}
              />

              <div className="p-2 text-xl h-24  font-serif">{item.name}</div>
            </div>
          );
        })}
      </div>
      <button onClick={handleLoadMore}>
        <span className="bg-yellow-500 rounded-xl p-2 text-black font-serif font-bold">
          Load More
        </span>
      </button>
    </div>
  );
};

export default Actors;
