import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchData } from "../../utils/utils";
import { loadingMore } from "../../slices/AppSlice";
import { setMovieReviews } from "../../slices/AppSlice";
import MovieHeader from "./MovieHeader";

const Reviews = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(
        `movie/${id}/reviews?language=en-US&page=1`
      );
      dispatch(setMovieReviews(result));
    }

    fetchDataFunction();
  }, [id]);
  const moviesReview = useSelector((state) => state.app.moviesReviews.data);
  const nextPage = useSelector((state) => state.app.loadMore.page);

  const handleLoadMore = async () => {
    dispatch(loadingMore());

    const res = await fetchData(
      `movie/${id}/reviews?language=en-US&page=${nextPage}`
    );
    dispatch(setMovieReviews([...moviesReview, ...res]));
  };

  return (
    <div className="flex flex-col gap-5">
      <MovieHeader />
      <div className="flex flex-col justify-center pl-10 pr-10 items-center gap-10">
        {moviesReview?.map((item, index) => {
          return (
            <div className="bg-gray-500">
              <div>
                {item.author_details.avatar_path ? (
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.author_details.avatar_path}`}
                    alt={`Profile of ${item.author}`}
                    className="w-[70px] rounded-3xl"
                  />
                ) : (
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Outline"
                    viewBox="0 0 24 24"
                    width="50"
                    height="50"
                  >
                    <path d="M19,0H5A5.006,5.006,0,0,0,0,5V19a5.006,5.006,0,0,0,5,5H19a5.006,5.006,0,0,0,5-5V5A5.006,5.006,0,0,0,19,0ZM7,22V21a5,5,0,0,1,10,0v1Zm15-3a3,3,0,0,1-3,3V21A7,7,0,0,0,5,21v1a3,3,0,0,1-3-3V5A3,3,0,0,1,5,2H19a3,3,0,0,1,3,3Z" />
                    <path d="M12,4a4,4,0,1,0,4,4A4,4,0,0,0,12,4Zm0,6a2,2,0,1,1,2-2A2,2,0,0,1,12,10Z" />
                  </svg>
                )}
              </div>

              <h3>A review by ${item.author}</h3>
              <div></div>

              <div>
                {" "}
                <p>{item.content}</p>
              </div>
            </div>
          );
        })}
        <button onClick={handleLoadMore}>
          <span className="bg-yellow-500 rounded-xl p-2 text-black font-serif font-bold">
            Load More
          </span>
        </button>
      </div>
    </div>
  );
};

export default Reviews;
