import React, { useEffect } from "react";
import { setSeasonsCast } from "../../../slices/TvShowSlice";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { fetchDetails } from "../../../utils/utils";

const SeasonCast = () => {
  const dispatch = useDispatch();
  const { id, season_number } = useParams();

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `tv/${id}/season/${season_number}/credits?language=en-US`
      );

      dispatch(setSeasonsCast({ data: result }));
    }

    fetchDataFunction();
  }, [id, season_number]);

  const seasonPeople = useSelector((state) => state.series?.seasonsCast?.data);

  return (
    <div>
      {seasonPeople.crew?.slice(0, 9).map((item, index) => {
        return <div>{item.name}</div>;
      })}
    </div>
  );
};

export default SeasonCast;
