import React, { useEffect } from "react";
import Modal from "react-modal";
import { openModal, closeModal } from "../../slices/AppSlice";
import { useSelector, useDispatch } from "react-redux";
import ReactPlayer from "react-player";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: 40,
    display: "flex",
    flexDirection: "column",
    gap: 30,
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.75)",
  },
};
const ModalComponent = ({ data, movieTrailer = false, tvTrailer = false }) => {
  useEffect(() => {
    Modal.setAppElement("#root");
  }, []);
  const dispatch = useDispatch();

  const open = useSelector((state) => state.app.modal.isOpen);

  function openModalFn() {
    dispatch(openModal());
  }

  function closeModalFn() {
    dispatch(closeModal());
  }

  return (
    <div>
      {data?.map((item, index) => {
        return (
          <div key={index}>
            {movieTrailer && (
              <div>
                <div className="bg-yellow-500 p-1 text-black font-bold rounded-xl flex flex-row gap-2 items-center">
                  {" "}
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Filled"
                    viewBox="0 0 24 24"
                    width="20"
                    height="20"
                    onClick={openModalFn}
                  >
                    <path d="M20.492,7.969,10.954.975A5,5,0,0,0,3,5.005V19a4.994,4.994,0,0,0,7.954,4.03l9.538-6.994a5,5,0,0,0,0-8.062Z" />
                  </svg>
                  <button onClick={openModalFn}>Watch Trailer</button>
                </div>

                <Modal
                  isOpen={open}
                  onRequestClose={closeModalFn}
                  style={customStyles}
                  contentLabel="Example Modal"
                  className="z-[2]"
                >
                  <ReactPlayer
                    url={`https://www.youtube.com/watch?v=${item.key}`}
                    controls={true}
                  />
                </Modal>
              </div>
            )}
            {tvTrailer && (
              <div>
                <div className="bg-yellow-500 p-1 text-black font-bold rounded-xl flex flex-row gap-2 items-center">
                  {" "}
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Filled"
                    viewBox="0 0 24 24"
                    width="20"
                    height="20"
                    onClick={openModalFn}
                  >
                    <path d="M20.492,7.969,10.954.975A5,5,0,0,0,3,5.005V19a4.994,4.994,0,0,0,7.954,4.03l9.538-6.994a5,5,0,0,0,0-8.062Z" />
                  </svg>
                  <button onClick={openModalFn}>Watch Trailer</button>
                </div>

                <Modal
                  isOpen={open}
                  onRequestClose={closeModalFn}
                  style={customStyles}
                  contentLabel="Example Modal"
                >
                  <ReactPlayer
                    url={`https://www.youtube.com/watch?v=${item.key}`}
                    controls={true}
                  />
                </Modal>
              </div>
            )}
          </div>
        );
      })}
    </div>
  );
};

export default ModalComponent;
