import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingMoviesToday } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";

import { Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const CarouselComponentNow = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/all/day?language=en-US");
      dispatch(trendingMoviesToday(res));
    }
    fetchDataFunction();
  }, []);
  const trendingMovieToday = useSelector(
    (state) => state.app.trendingToday.data
  );

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={7}
        spaceBetween={0}
        mousewheel={true}
        // pagination={{
        //   clickable: true,
        // }}
        scrollbar={{
          hide: true,
        }}
        modules={[Mousewheel, Pagination, Scrollbar]}
      >
        {trendingMovieToday?.map((item, index) => {
          return (
            <SwiperSlide>
              <div className="flex flex-col p-2">
                <div key={index}>
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="h-auto rounded-xl"
                  />
                </div>
                <div className="flex flex-col items-start ">
                  <span className="text-sm font-bold rounded-2xl bg-gray-600 p-2 relative flex justify-center">
                    {item.vote_average.toFixed(1)}
                  </span>
                  <h2 className="italic text-lg">{item.title || item.name}</h2>
                  <p className="text-xs text-gray-400">
                    {item.release_date || item.first_air_date}
                  </p>
                </div>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponentNow;
