import { createSlice } from "@reduxjs/toolkit";
import { act } from "react-dom/test-utils";

const initialState = {
  modal: {
    isOpen: false,
    // modalType:''
  },
  appData: {
    data: null,
  },

  trendingMovie: {
    data: null,
  },
  trendingTv: {
    data: null,
  },
  nowPlaying: {
    data: null,
  },
  popularMovies: {
    data: null,
  },
  topRatedMovies: {
    data: null,
  },
  upComing: {
    data: null,
  },
};

export const AppSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    openModal: (state, action) => {
      console.log("deschid modala");
      state.modal.isOpen = true;
    },
    closeModal: (state, action) => {
      console.log("inchid modala");
      state.modal.isOpen = false;
    },
    trendingMovies: (state, action) => {
      state.trendingMovie.data = action.payload;
    },
    trendingTvShows: (state, action) => {
      state.trendingTv.data = action.payload;
    },

    nowPlayingMovies: (state, action) => {
      state.nowPlaying.data = action.payload;
    },

    popularMovie: (state, action) => {
      state.popularMovies.data = action.payload;
    },

    topRatedMovie: (state, action) => {
      state.topRatedMovies.data = action.payload;
    },
  },
});

export const {
  openModal,
  closeModal,
  getCategoryData,
  getAppData,
  trendingMovies,
  trendingTvShows,
} = AppSlice.actions;

export default AppSlice.reducer;
