import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";

import { Mousewheel, Pagination } from "swiper/modules";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/movie/day?language=en-US");
      dispatch(trendingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const trendingMovie = useSelector((state) => state.app.trending.data);

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={10}
        spaceBetween={0}
        mousewheel={true}
        // pagination={{
        //   clickable: true,
        // }}
        modules={[Mousewheel, Pagination]}
      >
        {trendingMovie?.map((item, index) => {
          console.log(item.vote_average.toFixed(1));
          return (
            <SwiperSlide>
              <div key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                  className="w-[100px] h-auto p-2 border-2"
                />
              </div>
              <div className="text-xs flex justify-center">
                <span className="rounded-2xl bg-red-500 p-2">
                  {item.vote_average}
                </span>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
