import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { useParams } from "react-router-dom";

const Episodes = () => {
  const { id, season_number } = useParams();

  const seasonsDetailed = useSelector((state) => state.series.tvSeason.data);
  return (
    <div className="grid grid-cols-4 gap-2 ">
      {seasonsDetailed.episodes?.map((item, index) => {
        return (
          <Link
            to={`/tvshow/${id}/season/${season_number}/episode/${item.episode_number}`}
            key={index}
            className="flex gap-4 flex-row justify-start items-center rounded-2xl border-yellow-500 border-t border-r border-b"
          >
            <span className="bg-yellow-500 font-serif font-bold text-lg p-1 rounded-2xl text-black border-2 border-yellow-500">
              Episode {item.episode_number}
            </span>
            <span className="text-xs">{item.name}</span>
          </Link>
        );
      })}
    </div>
  );
};

export default Episodes;
