import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";

import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const CarouselComponentMovieNow = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/movie/day?language=en-US");
      dispatch(trendingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const trendingMoviesNow = useSelector(
    (state) => state.app.trendingMovie.data
  );

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={6}
        spaceBetween={0}
        // mousewheel={true}
        // pagination={{
        //   clickable: true,
        // }}
        // autoplay={{
        //   delay: 3000,
        //   disableOnInteraction: false,
        // }}
        scrollbar={{
          hide: true,
        }}
        modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
      >
        {trendingMoviesNow?.map((item, index) => {
          return (
            <SwiperSlide>
              <div className="flex flex-col p-2 h-[500px]">
                <div key={index} className="h-[70%]">
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="h-auto rounded-xl"
                  />
                </div>
                <span className="text-sm font-bold rounded-2xl bg-yellow-400 p-2 flex justify-center text-black">
                  {item.vote_average.toFixed(1)}
                </span>
                <div className="flex flex-col items-start gap-3">
                  <h2 className="italic font-bold text-lg h-18">
                    {item.title}
                  </h2>
                  <p className="text-xs text-gray-500">{item.release_date}</p>
                </div>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponentMovieNow;
