import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);
  return (
    <>
      {movieDetails && (
        <div className="pl-10 pr-10">
          <div className="flex flex-row gap-5">
            <img
              src={`https://image.tmdb.org/t/p/original${movieDetails.poster_path}`}
              alt={movieDetails.title}
              className="w-[300px] h-auto rounded-xl"
            />

            <div className="flex flex-col">
              <div>
                <span className="text-2xl font-serif italic text-yellow-500">
                  {movieDetails.title} (
                  {movieDetails.release_date.split("-")[0]})
                </span>
              </div>
              <div className="flex flex-row">
                <span>{movieDetails.vote_average.toFixed(1)}</span>
                <span>{movieDetails.release_date}</span>
                {movieDetails.genres?.map((item, index) => {
                  return (
                    <ul key={index} className="list-disc inline-block">
                      <li>{item.name}</li>
                    </ul>
                  );
                })}
              </div>
              <div className="flex flex-col">
                <span className="text-lg font-serif italic underline">
                  Overview
                </span>
                <span className="text-xs font-serif">
                  {movieDetails.overview}
                </span>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default MoviesDetails;
