import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { movies } from "../../slices/AppSlice";
import { fetchData } from "../../utils/utils";
import { loadMoreMovie } from "../../slices/AppSlice";

const Movies = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData(`/movie/now_playing?language=en-US&page=1`);
      dispatch(movies(res));
    }

    fetchDataFunction();
  }, []);

  const moviesList = useSelector((state) => state.app.movie.data);

  const handleLoadMore = async () => {
    dispatch(loadMoreMovie());
    const nextPage = useSelector((state) => state.app.movie.page);
    const res = await fetchData(
      `/movie/now_playing?language=en-US&page=${nextPage}`
    );
    dispatch(movies([...moviesList, ...res]));
  };

  return (
    <div className="pl-10 pr-10 flex flex-col gap-2">
      <span className="text-2xl font-serif">Movies</span>
      <div className="flex flex-row">
        <div className="w-[30%]">Filtrare</div>
        <div className="grid grid-cols-4 w-[70%] gap-5">
          {moviesList?.map((item, index) => {
            return (
              <div key={index} className="flex flex-col rounded-xl bg-gray-700">
                <img
                  className="rounded-xl flex items-center"
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.title}
                />

                <div className="p-2 text-xl h-24  font-serif">{item.title}</div>
                <div className="text-xs text-gray-400 p-2 font-serif h-18 items-center">
                  {item.release_date}
                </div>
              </div>
            );
          })}
        </div>
      </div>
      <button
        onClick={() => {
          dispatch(loadMoreMovie());
        }}
      >
        Load More
      </button>
    </div>
  );
};
export default Movies;
