import React from "react";
import { useSelector } from "react-redux";
import MovieHeader from "./MovieHeader";
import { Link } from "react-router-dom";

const MovieFullCast = () => {
  const fullMovieCast = useSelector((state) => state.movie.moviesActors.data);

  return (
    <div className="flex gap-5 flex-col pl-10 pr-10">
      <MovieHeader />
      {fullMovieCast && (
        <div>
          <div className="grid grid-cols-2 gap-5 ">
            <div className="flex flex-col gap-3 items-center">
              <span className="font-bold underline text-xl italic font-serif">
                Cast
              </span>
              <div className="flex flex-col gap-2">
                {fullMovieCast.cast?.map((item, index) => {
                  return (
                    <Link
                      to={`/actor/${item.id}`}
                      className="flex flex-row gap-2"
                    >
                      {item.profile_path ? (
                        <div className="flex flex-row gap-3">
                          <div key={index}>
                            <img
                              src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                              alt={item.name}
                              className="w-[100px] h-auto"
                            />
                          </div>
                          <div className="flex flex-col">
                            <span className="text-bold text-xl">
                              {item.name}
                            </span>
                            <span className="text-sm text-gray-500">
                              {item.character}
                            </span>
                          </div>
                        </div>
                      ) : (
                        <></>
                      )}
                    </Link>
                  );
                })}
              </div>
            </div>
            <div className="flex flex-col gap-3 items-center">
              <span className="font-bold font-serif text-xl italic underline">
                Crew
              </span>
              <div className="flex flex-col gap-2">
                {fullMovieCast.crew?.map((item, item_index) => {
                  return (
                    <div className="flex flex-row gap-2">
                      {item.profile_path ? (
                        <div key={item_index}>
                          <img
                            src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                            alt={item.name}
                            className="w-[100px] h-auto"
                          />
                        </div>
                      ) : (
                        <svg
                          xmlns="http://www.w3.org/2000/svg"
                          id="Layer_1"
                          data-name="Layer 1"
                          viewBox="0 0 24 24"
                          width="100"
                          height="150"
                          className="bg-gray-400"
                        >
                          <path d="M20.1,5.39l-3.49-3.49c-1.23-1.23-2.86-1.9-4.6-1.9H6.5C4.02,0,2,2.02,2,4.5v15c0,2.48,2.02,4.5,4.5,4.5h11c2.48,0,4.5-2.02,4.5-4.5V9.99c0-1.74-.68-3.37-1.9-4.6Zm-.71,.71c.55,.55,.97,1.2,1.24,1.9h-5.13c-.83,0-1.5-.67-1.5-1.5V1.37c.71,.27,1.35,.69,1.9,1.24l3.49,3.49ZM6.5,1h5.51c.33,0,.66,.03,.99,.09V6.5c0,1.38,1.12,2.5,2.5,2.5h5.41c.06,.32,.09,.65,.09,.99v5.3l-2.79-2.79c-.65-.65-1.78-.65-2.43,0l-3.27,3.27c-.27,.27-.74,.27-1.02,0l-3.27-3.27c-.65-.65-1.78-.65-2.43,0l-2.79,2.79V4.5c0-1.93,1.57-3.5,3.5-3.5Zm11,22H6.5c-1.93,0-3.5-1.57-3.5-3.5v-2.79l3.5-3.5c.27-.27,.74-.27,1.02,0l3.27,3.27c.32,.32,.76,.5,1.21,.5s.89-.18,1.22-.5l3.27-3.27c.27-.27,.74-.27,1.02,0l3.49,3.49v2.8c0,1.93-1.57,3.5-3.5,3.5Z" />
                        </svg>
                      )}
                      <div className="flex flex-col gap-2">
                        <span className="text-bold text-xl">{item.name}</span>
                        <span className="text-sm text-gray-500">
                          {item.known_for_department}
                        </span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default MovieFullCast;
