import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { useParams } from "react-router-dom";
import { setMoviesDetails } from "../../slices/AppSlice";
import { fetchData } from "../../utils/utils";

const MoviesDetails = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchMovieDetail = async () => {
      const res = await fetchData(`/movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(res));
    };
    fetchMovieDetail();
  }, [id, dispatch]);

  const movieDetails = useSelector((state) => state.app.movieDetail.data);

  return (
    <div>
      {movieDetails?.map((item, index) => {
        return (
          <div>
            <h1>{movieDetails.title}</h1>
            <p>{movieDetails.overview}</p>
          </div>
        );
      })}
    </div>
  );
};

export default MoviesDetails;
