import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { upComingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";

import { Mousewheel, Pagination } from "swiper/modules";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/all/day?language=en-US");
      dispatch(upComingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={3}
        spaceBetween={5}
        mousewheel={true}
        // pagination={{
        //   clickable: true,
        // }}
        modules={[Mousewheel, Pagination]}
        className="w-screen pl-5"
      >
        {upComingMovie?.map((item, index) => {
          return (
            <SwiperSlide>
              <div key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={""}
                  className="w-[300px] h-auto border-4"
                />
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
