import React, { useState } from "react";
import { fetchData } from "../../utils/utils.js";
import SearchBar from "../(utils)/SearchBar";

const SerieSearchBar = () => {
  const [search, setSearch] = useState("");
  const [serieResult, setSerieResult] = useState([]);

  const handlerSearch = async (e) => {
    if (e.key === "Enter") {
      const res = await fetchData(
        `search/tv?query=${search}&include_adult=false&language=en-US&page=1`
      );
      setSerieResult(res);
    }
  };

  return (
    <div className="relative w-[450px]">
      <div className="flex flex-row gap-2 ">
        <input
          className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl w-[100%] "
          type="search"
          placeholder="Search Movie / Tv Show / Actors "
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />
      </div>
      <SearchBar data={serieResult} movieResult={true} />
    </div>
  );
};

export default SerieSearchBar;
