import React, { useEffect, useState } from "react";
import { setSearchedMovie } from "../../slices/MovieSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";

const SearchBar = () => {
  const dispatch = useDispatch([]);
  //   const { title } = useParams();
  const [search, setSearch] = useState("");

  useEffect(() => {
    async function fetchDataFunction(search) {
      const result = await fetchDetails(
        `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
      );
      dispatch(setSearchedMovie(result));
    }

    fetchDataFunction(search);
  }, [dispatch]);

  const movieSearch = useSelector((state) => state.movie.searchedMovie.data);

  const handlerSearch = (e) => {
    setSearch((e) => e.target.value);
  };

  return (
    <div className="flex flex-row items-center bg-white rounded-xl">
      <input
        className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl"
        type="search"
        size="50"
        placeholder="Search Movie / Tv Show / Actors "
        value={search}
        // onChange={(e) => setSearch(e.target.value)}
      />

      <button
        className="text-black font-bold p-2 bg-yellow-500  rounded-xl"
        onClick={handlerSearch}
      >
        Search
      </button>
    </div>
  );
};

export default SearchBar;
