import React, { useEffect, useState } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { upComingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import {
  Pagination,
  Autoplay,
  Navigation,
  Scrollbar,
  FreeMode,
  Thumbs,
} from "swiper/modules";
import "swiper/css/scrollbar";
import "swiper/css";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/all/day?language=en-US");
      dispatch(upComingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);
  const [thumbsSwiper, setThumbsSwiper] = useState(null);
  return (
    <>
      <Swiper
        scrollbar={{
          hide: false,
        }}
        slidesPerView={1}
        autoplay={{
          delay: 5000,
          disableOnInteraction: false,
        }}
        loop={true}
        navigation={true}
        modules={[Pagination, Navigation, Autoplay, Scrollbar]}
        className="w-[400px]"
      >
        {upComingMovie?.map((item, index) => {
          return (
            <SwiperSlide>
              <div key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={""}
                  className="w-[300px] h-auto border-4"
                />
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
