import React, { useEffect, useState } from "react";
import { setSearchedMovie } from "../../slices/MovieSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";
import { setMovieQuery } from "../../slices/MovieSlice";

const SearchBar = () => {
  const dispatch = useDispatch([]);
  const query = useSelector((state) => state.movie.searchedMovie.movieQuery);

  //   useEffect(() => {
  //     async function fetchDataFunction() {
  //       const result = await fetchDetails(
  //         `search/movie?query=${query}&include_adult=false&language=en-US&page=1`
  //       );
  //       dispatch(setSearchedMovie(result));
  //     }

  //     fetchDataFunction();
  //   }, [dispatch]);

  const searchMovies = async (value) => {
    const res = await fetchDetails(
      `search/movie?query=${value}&include_adult=false&language=en-US&page=1`
    );
    dispatch(setSearchedMovie(res));
  };

  const movieSearch = useSelector((state) => state.movie.searchedMovie.data);

  const handlerSearch = () => {
    setSearchedMovie();
  };

  return (
    <div className="flex flex-row items-center bg-white rounded-xl">
      <input
        className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl"
        type="search"
        size="50"
        placeholder="Search Movie / Tv Show / Actors "
        value={query}
        onChange={(e) => setMovieQuery(e.target.value)}
      />

      <button
        className="text-black font-bold p-2 bg-yellow-500  rounded-xl"
        onClick={handlerSearch}
      >
        Search
      </button>
    </div>
  );
};

export default SearchBar;
