import React from "react";
import { useSelector } from "react-redux";
import { setTvCast } from "../../../slices/TvShowSlice";

const CastPeople = () => {
  const fullCastPeople = useSelector((state) => state.series.tvActors.data);

  return (
    <div>
      {fullCastPeople && (
        <div className="flex flex-col gap-3 items-center">
          <span className="font-bold underline text-xl italic font-serif">
            Cast
          </span>
          <div className="flex flex-col gap-2">
            {fullCastPeople.cast?.map((item, index) => {
              return (
                <div key={index}>
                  {item.profile_path ? (
                    <div className="flex flex-row gap-2">
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.name}
                        className="w-[100px] h-auto"
                      />

                      <div className="flex flex-col">
                        <span className="font-bold text-xl">{item.name}</span>
                        {item.roles && (
                          <div>
                            {item.roles?.map((role, index) => {
                              return (
                                <div key={index} className="flex flex-col">
                                  <span className="text-lg text-gray-300 italic">
                                    as
                                  </span>
                                  <span className="text-lg text-gray-300 italic">
                                    {role.character}
                                  </span>
                                  <span className="text-sm text-gray-500 ">
                                    ({role.episode_count} Episodes)
                                  </span>
                                </div>
                              );
                            })}
                          </div>
                        )}
                      </div>
                    </div>
                  ) : (
                    <div></div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
};

export default CastPeople;
