import React from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import MovieKeywords from "../movies/MovieKeywords";
import TvShowKeywords from "../tvshow/TvShowKeywords";
import ModalComponent from "./ModalComponent";

const DetailsComponent = ({
  data,
  serieDetail = false,
  movieDetail = false,
  seasonDetails = false,
  episodeDetails = false,
  id = null,
}) => {
  const trailerMovie = useSelector((state) => state.movie.movieTrailer.data);
  const trailerShow = useSelector((state) => state.series.tvTrailer.data);
  return (
    <div>
      {seasonDetails && (
        <div className=" flex flex-row gap-4">
          {data.poster_path || data.still_path ? (
            <img
              src={`https://image.tmdb.org/t/p/original${
                data.poster_path ? data.poster_path : data.still_path
              }`}
              alt={data.name}
              className="w-auto h-[400px]"
            />
          ) : (
            <svg
              xmlns="http://www.w3.org/2000/svg"
              id="Layer_1"
              data-name="Layer 1"
              viewBox="0 0 24 24"
              width="200"
              height="400"
