import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { setMovieTrailer } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

const Trailer = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(`movie/${id}/videos?language=en-US`);
      dispatch(setMovieTrailer(result));
    }

    fetchDataFunction();
  }, [id]);

  const moviesTrailers = useSelector((state) => state.app.movieTrailer.data);

  return (
    <>
      {moviesTrailers?.map((item, index) => {
        return (
          <div key={index}>
            {item.type == "Trailer"} && (
            {`https://www.youtube.com/watch?v=${item.id}`})
          </div>
        );
      })}
    </>
  );
};

export default Trailer;
