import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { fetchDetails } from "../../../utils/utils";
import { setTvSeason } from "../../../slices/TvShowSlice";
import TvHeader from "../TvHeader";

const SeasonDetails = () => {
  const dispatch = useDispatch();
  const { id, seasonNumber } = useParams();
  const tvDetails = useSelector((state) => state.series.tvDetail.data);

  useEffect(() => {
    const fetchDataFunction = async () => {
      // Check if 'seasons' property exists in tvSeasonNumber and it's an array
      if (tvDetails.seasons && Array.isArray(tvDetails.seasons)) {
        // Find the season with the matching season_number
        const selectedSeason = tvDetails.seasons.find(
          (season) => season.season_number === Number(seasonNumber)
        );

        if (selectedSeason) {
          const result = await fetchDetails(
            `tv/${id}/season/${selectedSeason.season_number}?language=en-US`
          );
          dispatch(setTvSeason([result])); // Wrap result in an array to maintain consistency
        }
      }
    };

    fetchDataFunction();
  }, [id, seasonNumber, tvDetails, dispatch]);

  const seasonsDetailed = useSelector((state) => state.series.tvSeason.data);

  return (
    <div className="flex flex-col gap-5 pl-10 pr-10">
      <TvHeader />
      {seasonsDetailed.seasons && (
        <div className="flex flex-col gap-10 ">
          {seasonsDetailed.seasons?.map((item, index) => {
            return (
              <div className="flex flex-row border-2 gap-5 p-3" key={index}>
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={item.name}
                  className="w-[200px] rounded-2xl"
                />
                <div className="flex flex-col gap-2 ">
                  <div className="flex flex-row justify-between items-center">
                    <span className="text-yellow-500 font-bold text-xl">
                      {item.name}
                    </span>
                    <span className="bg-yellow-500 p-1 rounded-2xl text-black font-bold flex flex-row items-center gap-2">
                      <svg
                        xmlns="http://www.w3.org/2000/svg"
                        id="Layer_1"
                        data-name="Layer 1"
                        viewBox="0 0 24 24"
                        width="18"
                        height="18"
                      >
                        <path d="M12,24C5.38,24,0,18.62,0,12S5.38,0,12,0s12,5.38,12,12-5.38,12-12,12ZM12,1C5.93,1,1,5.93,1,12s4.93,11,11,11,11-4.93,11-11S18.07,1,12,1Zm-3.26,16c-.21,0-.42-.07-.6-.2-.34-.25-.48-.7-.35-1.1l.94-3.01-2.37-1.93c-.33-.28-.44-.72-.3-1.11,.14-.39,.52-.65,.94-.65h3l1.06-2.85c.14-.38,.51-.62,.94-.62s.8,.24,.94,.62l1.06,2.85h3c.42,0,.8,.26,.94,.66h0c.14,.39,.03,.84-.29,1.1l-2.37,1.93,.98,2.98c.13,.4,0,.85-.34,1.1-.34,.26-.8,.28-1.15,.05l-2.75-1.79-2.71,1.81c-.17,.11-.37,.17-.56,.17Zm-1.74-7.01l2.62,2.13c.16,.13,.22,.34,.16,.54l-1.04,3.34,2.98-1.99c.17-.11,.38-.11,.55,0l3.03,1.97-1.09-3.31c-.07-.2,0-.41,.16-.54l2.62-2.13h-3.35c-.21,0-.4-.13-.47-.33l-1.18-3.18-1.18,3.18c-.07,.2-.26,.33-.47,.33h-3.35Zm10,0h0Zm0,0h0s0,0,0,0Z" />
                      </svg>
                      {item.vote_average}
                    </span>
                  </div>
                  <span className="text-lg italic underline font-bold">
                    Overview
                  </span>
                  <span className="text-sm italic">{item.overview}</span>
                  <span className="text-lg font-bold text-yellow-500">
                    Total Episodes: {item.episode_count}
                  </span>
                  <div>
                    {" "}
                    <button
                      key={index}
                      className=" bg-gray-400 p-2 rounded-2xl"
                    >
                      See More
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      )}

      <div></div>
    </div>
  );
};

export default SeasonDetails;
