import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  trendingMovie: {
    data: null,
  },
  trendingTv: {
    data: null,
  },

  loadMore: {
    page: 1,
  },
};

export const AppSlice = createSlice({
  name: "app",
  initialState,
  reducers: {
    trendingMovies: (state, action) => {
      state.trendingMovie.data = action.payload;
    },
    trendingTvShows: (state, action) => {
      state.trendingTv.data = action.payload;
    },

    loadingMore: (state) => {
      state.loadMore.page += 1;
    },
  },
});

export const { trendingMovies, trendingTvShows, loadingMore } =
  AppSlice.actions;

export default AppSlice.reducer;
