import React, { useEffect } from "react";
import { fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setSimilarTv } from "../../slices/TvShowSlice";
import { loadingMore } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css/scrollbar";
import { Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const SimilarTv = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(
        `tv/${id}/similar?language=en-US&page=1`
      );
      dispatch(setSimilarTv(result));
    }

    fetchDataFunction();
  }, [id]);

  const similarTvShow = useSelector((state) => state.series.tvSimilar.data);

  return (
    <>
      <span className="bg-yellow-500 p-1 text-black text-2xl font-bold font-serif rounded-xl">
        Recommendations
      </span>
      {similarTvShow.results && (
        <Swiper
          direction={"horizontal"}
          slidesPerView={3}
          spaceBetween={0}
          mousewheel={true}
          modules={[Mousewheel, Pagination, Scrollbar]}
          className="w-[100%] border-2"
        >
          {similarTvShow.results?.map((item, index) => {
            return (
              <SwiperSlide className="p-2 h-[200px] " key={index}>
                <div className="h-[70%] mb-5">
                  {item.backdrop_path ? (
                    <img
                      src={`https://image.tmdb.org/t/p/original${item.backdrop_path}`}
                      alt={item.name}
                      className="rounded-xl "
                    />
                  ) : (
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      id="Layer_1"
                      data-name="Layer 1"
                      viewBox="0 0 24 24"
                      width="30"
                      height="20"
                    >
                      <path d="M20.1,5.39l-3.49-3.49c-1.23-1.23-2.86-1.9-4.6-1.9H6.5C4.02,0,2,2.02,2,4.5v15c0,2.48,2.02,4.5,4.5,4.5h11c2.48,0,4.5-2.02,4.5-4.5V9.99c0-1.74-.68-3.37-1.9-4.6Zm-.71,.71c.55,.55,.97,1.2,1.24,1.9h-5.13c-.83,0-1.5-.67-1.5-1.5V1.37c.71,.27,1.35,.69,1.9,1.24l3.49,3.49ZM6.5,1h5.51c.33,0,.66,.03,.99,.09V6.5c0,1.38,1.12,2.5,2.5,2.5h5.41c.06,.32,.09,.65,.09,.99v5.3l-2.79-2.79c-.65-.65-1.78-.65-2.43,0l-3.27,3.27c-.27,.27-.74,.27-1.02,0l-3.27-3.27c-.65-.65-1.78-.65-2.43,0l-2.79,2.79V4.5c0-1.93,1.57-3.5,3.5-3.5Zm11,22H6.5c-1.93,0-3.5-1.57-3.5-3.5v-2.79l3.5-3.5c.27-.27,.74-.27,1.02,0l3.27,3.27c.32,.32,.76,.5,1.21,.5s.89-.18,1.22-.5l3.27-3.27c.27-.27,.74-.27,1.02,0l3.49,3.49v2.8c0,1.93-1.57,3.5-3.5,3.5Z" />
                    </svg>
                  )}
                </div>
                <div className="h-[30%] flex flex-row justify-between items-center gap-1">
                  <span className="text-xs font-serif italic">{item.name}</span>
                  <span className="bg-yellow-500 text-black p-1 rounded-xl text-sm">
                    {item.vote_average ? item.vote_average.toFixed(1) : "0"}
                  </span>
                </div>
              </SwiperSlide>
            );
          })}
        </Swiper>
      )}
    </>
  );
};

export default SimilarTv;
