import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { moviesDetails } from "../../slices/AppSlice";

const MoviesDetails = () => {
  const dispatch = useDispatch;
  const fetchMovieDetails = async (movieID) => {
    const res = await fetchData(`/movie/${movieID}?language=en-US`);
    dispatch(moviesDetails(res));
  };
  fetchMovieDetails();

  const movieDetails = useSelector((state) => state.moviesDetails.data);
};

export default MoviesDetails;
