import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Link } from "react-router-dom";
import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";
import "swiper/css";

import ModalComponent from "./ModalComponent";
import { useSelector } from "react-redux";
import Trailer from "../movies/Trailer";

const SwiperComponent = ({
  data,
  seasonsListing = false,
  id = null,
  movieCard = false,
  seasonCast = false,
  serieCard = false,
  serieCast = false,
  episodeCast = false,
  movieCast = false,
  episodeGuests = false,
  actorsCredits = false,
  movieVideos = false,
  similarShow = false,

  slidesPerView = null,
  autoplay: { delay = 5000, disableOnInteraction = false },
  scrollbar = false,
  mousewheel = false,
}) => {
  return (
    <Swiper
      direction={"horizontal"}
      slidesPerView={6}
      spaceBetween={0}
      autoplay={(delay, { disableOnInteraction })}
      scrollbar={scrollbar}
      mousewheel={mousewheel}
      modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
    >
      {data?.map((item, index) => {
        return (
          <SwiperSlide key={index}>
            {movieCard && (
              <div className="flex flex-col  h-[600px]  bg-gray-800 m-2 rounded-xl gap-2 ">
                <div
                  key={index}
                  className="h-[70%] flex flex-col justify-center"
                >
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="rounded-xl h-[100%] "
                  />
                </div>

                <div className="flex flex-col items-start  justify-between p-2 h-[30%] ">
                  <h2 className="italic font-bold text-lg ">{item.title}</h2>
                  <div className="flex flex-row justify-between items-center w-[100%]">
                    <p className="text-xs text-gray-500">{item.release_date}</p>
                    <span className="text-sm font-bold rounded-2xl bg-yellow-400 p-2  text-black">
                      {item.vote_average.toFixed(1)}
                    </span>
                  </div>
                </div>

                <Link
                  to={`/movie/${item.id}`}
                  className="flex flex-row items-center gap-3 bg-yellow-500 rounded-xl p-2 justify-center"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Layer_1"
                    data-name="Layer 1"
                    viewBox="0 0 24 24"
                    width="20"
                    height="20"
                  >
