import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  movies: {
    data: [],
  },
  movieDetail: {
    data: [],
  },
  movieTrailer: {
    data: null,
  },
  moviesActors: {
    data: [],
  },
  moviesReviews: {
    data: [],
  },
  movieKeyword: {
    data: [],
  },
};

export const MovieSlice = createSlice({
  name: "movie",
  initialState,
  reducers: {
    setMovies: (state, action) => {
      state.movies.data = action.payload;
    },
    setMoviesDetails: (state, action) => {
      state.movieDetail.data = action.payload;
    },

    setMovieCast: (state, action) => {
      state.moviesActors.data = action.payload;
    },
    setMovieReviews: (state, action) => {
      state.moviesReviews.data = action.payload;
    },
    setMovieKeyword: (state, action) => {
      state.movieKeyword.data = action.payload;
    },
    setMovieTrailer: (state, action) => {
      state.movieTrailer.data = state.movieTrailer
        ?.filter(({ type }) => type === "Trailer")
        .data(action.payload);
    },
  },
});

export const {
  setMovies,
  setMoviesDetails,
  setMovieCast,
  setMovieReviews,
  setMovieKeyword,
  setMovieTrailer,
} = MovieSlice.actions;

export default MovieSlice.reducer;
