import React, { useEffect } from "react";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { moviesDetails } from "../../slices/AppSlice";
import { movieDetail } from "../../slices/AppSlice";

const MoviesDetails = () => {
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchMovieDetails = async (movieID) => {
      const res = await fetchData(`/movie/${movieID}?language=en-US`);
      dispatch(moviesDetails(res));
    };
    fetchMovieDetails();
  }, []);

  const movieDetails = useSelector((state) => state.movieDetail.data);

  return (
    <div>
      {movieDetails && (
        <div>
          <h1>{movieDetails.original_title}</h1>
          <p>{movieDetails.overview}</p>
          {/* Add more movie details as needed */}
        </div>
      )}
    </div>
  );
};

export default MoviesDetails;
