import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast, setMoviesDetails } from "../../slices/AppSlice";
import { useParams } from "react-router-dom";
import BackArrow from "../../images/arrow-left.svg";

const FullCast = () => {
  const dispatch = useDispatch();
  const { id, title } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }
    fetchDataFunction();
  }, [id, title]);

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const fullMovieCast = useSelector((state) => state.app.moviesActors.data);
  const movieDetails = useSelector((state) => state.app.movieDetail.data);

  return (
    <>
      <div className="flex flex-col items-center">
        {" "}
        {movieDetails && (
          <span className="bg-yellow-500 font-bold text-4xl text-black font-serif p-1">
            {movieDetails.title}
          </span>
        )}
        <div className="bg-yellow-500">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            id="Bold"
            viewBox="0 0 24 24"
            width="512"
            height="512"
          >
            <path d="M4.943,5.606,1.024,9.525a3.585,3.585,0,0,0,0,4.95l3.919,3.919a1.5,1.5,0,1,0,2.121-2.121L4.285,13.492l18.25-.023a1.5,1.5,0,0,0,1.5-1.5v0a1.5,1.5,0,0,0-1.5-1.5L4.3,10.492,7.064,7.727A1.5,1.5,0,0,0,4.943,5.606Z" />
          </svg>

          <span>Back to main</span>
        </div>
      </div>

      {fullMovieCast && <div>{fullMovieCast.name}</div>}
    </>
  );
};

export default FullCast;
