import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { upComingMovies } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination, Autoplay } from "swiper/modules";
// Import Swiper styles
import "swiper/css";

const CarouselComponent = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData(
        "https://api.themoviedb.org/3/trending/all/day?language=en-US"
      );
      dispatch(upComingMovies(res));
    }
    fetchDataFunction();
  }, []);
  const upComingMovie = useSelector((state) => state.app.upComing.data);

  return (
    <>
      <Swiper
        modules={[Pagination, Autoplay]}
        spaceBetween={100}
        slidesPerView={2}
        // navigation
        pagination={{ dynamicBullets: true }}
        scrollbar={{ draggable: true }}
        onSwiper={(swiper) => console.log(swiper)}
        onSlideChange={() => console.log("slide change")}
        autoplay={{
          delay: 2500,
          disableOnInteraction: false,
        }}
      >
        {upComingMovie?.map((item, index) => {
          return (
            <SwiperSlide>
              <div
                key={index}
                className="border-2 bg-black rounded-xl p-10 h-screen "
              >
                <img
                  src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                  alt={""}
                  className="w-[400px] h-auto aspect-auto"
                />
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponent;
