import React, { useEffect, useState } from "react";
import { setSearchedMovie } from "../../slices/MovieSlice";
import { useDispatch, useSelector } from "react-redux";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useParams } from "react-router-dom";
import { setMovieQuery } from "../../slices/MovieSlice";

const SearchBar = () => {
  const [search, setSearch] = useState("");
  const [result, setResult] = useState([]);

  const handlerSearch = async (e) => {
    if (e.key === "Enter") {
      const res = fetchData(
        `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
      );
      setResult(res);
    }
  };

  return (
    <div className="flex flex-col">
      <div className="flex flex-row items-center bg-white rounded-xl">
        <input
          className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl"
          type="search"
          size="50"
          placeholder="Search Movie / Tv Show / Actors "
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />

        {/* <button
          className="text-black font-bold p-2 bg-yellow-500  rounded-xl"
          onClick={handlerSearch}
        >
          Search
        </button> */}
      </div>
      <div className="">
        <img
          src={`https://image.tmdb.org/t/p/original${result.poster_path}`}
          alt={result.name}
        />
      </div>
    </div>
  );
};

export default SearchBar;
