import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { openModal, closeModal } from "../../slices/AppSlice";
import { useSelector, useDispatch } from "react-redux";

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
    padding: 40,
    display: "flex",
    flexDirection: "column",
    gap: 30,
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.75)",
  },
};

const ModalComponent = () => {
  useEffect(() => {
    Modal.setAppElement("#root");
  }, []);
  const dispatch = useDispatch();

  const open = useSelector((state) => state.app.modal.isOpen);

  function openModalFn() {
    dispatch(openModal());
  }

  // function afterOpenModal() {
  //   // references are now sync'd and can be accessed.
  //   subtitle.style.color = "#f00";
  // }

  function closeModalFn() {
    dispatch(closeModal());
  }
  return (
    <div>
      <button onClick={openModalFn} className="text-sm p-1 border">
        Sign In
      </button>
      <Modal
        isOpen={open}
        // onAfterOpen={afterOpenModal}
        onRequestClose={closeModalFn}
        style={customStyles}
        contentLabel="Example Modal"
      >
        <form className="flex flex-col gap-4 items-center text-sm ">
          <label>Email Adress</label>
          <input
            type="email"
            placeholder="Enter your email"
            className="border-2 rounded-xl p-1"
          />
          <label>Password</label>
          <input
            type="password"
            placeholder="Enter your password"
            className="border-2 rounded-xl p-1"
          />
          <button onClick={closeModalFn}>Log In</button>
        </form>
      </Modal>
    </div>
  );
};

export default ModalComponent;
