import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast, setMoviesDetails } from "../../slices/AppSlice";
import { Link, useParams } from "react-router-dom";

const FullCast = () => {
  const dispatch = useDispatch();
  const { id, title } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }
    fetchDataFunction();
  }, [id, title]);

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const fullMovieCast = useSelector((state) => state.app.moviesActors.data);
  const movieDetails = useSelector((state) => state.app.movieDetail.data);

  return (
    <div className="flex gap-5 flex-col">
      <div className="flex flex-col items-center gap-5">
        {" "}
        {movieDetails && (
          <div className="flex flex-row items-center gap-2">
            <div>
              {" "}
              <img
                src={`https://image.tmdb.org/t/p/original${movieDetails.backdrop_path}`}
                alt={movieDetails.title}
                className="w-[300px] h-auto"
              />
            </div>
            <div className="flex flex-col gap-2">
              <span className="bg-yellow-500 font-bold text-4xl text-black font-serif p-1">
                {movieDetails.title}
              </span>
              <Link to={`/movie/${id}`}>
                <div className=" flex flex-row gap-2 items-center">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    id="Bold"
                    viewBox="0 0 24 24"
                    width="20"
                    height="20"
                    className="bg-yellow-500"
                  >
                    <path d="M4.943,5.606,1.024,9.525a3.585,3.585,0,0,0,0,4.95l3.919,3.919a1.5,1.5,0,1,0,2.121-2.121L4.285,13.492l18.25-.023a1.5,1.5,0,0,0,1.5-1.5v0a1.5,1.5,0,0,0-1.5-1.5L4.3,10.492,7.064,7.727A1.5,1.5,0,0,0,4.943,5.606Z" />
                  </svg>
                  <span>Back to main</span>
                </div>
              </Link>
            </div>
          </div>
        )}
      </div>

      {fullMovieCast && (
        <div>
          <div className="flex flex-col gap-3">
            <span className="bg-yellow-500 font-bold font-serif text-xl text-black">
              Cast
            </span>
            <div className="flex flex-col gap-2">
              {fullMovieCast.cast?.map((item, index) => {
                return (
                  <div className="flex flex-row gap-2">
                    <div key={index}>
                      <img
                        src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                        alt={item.name}
                        className="w-[50px] h-auto"
                      />
                    </div>
                    <div className="flex flex-col">
                      <span>{item.name}</span>
                      <span>{item.character}</span>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

          {fullMovieCast.crew?.map((item, index) => {
            return (
              <div>
                <span key={index}>{item.known_for_department}</span>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};

export default FullCast;
