import React, { useState } from "react";
import { fetchData, fetchDetails } from "../../utils/utils";
import { Link } from "react-router-dom";

const SearchBar = () => {
  const [search, setSearch] = useState("");
  const [movieResult, setMovieResult] = useState([]);
  const [seriesResult, setSeriesResult] = useState([]);
  const [actorsResult, setActorsResult] = useState([]);

  const handlerSearch = async (e) => {
    const result = await fetchData(
      `search/movie?query=${search}&include_adult=false&language=en-US&page=1`
    );
    // fetchData(
    //   `search/tv?query=${search}&include_adult=false&language=en-US&page=1`
    // );
    // fetchData(
    //   `search/person?query=${search}&include_adult=false&language=en-US&page=1`
    // );
    setMovieResult(result);
    // setSeriesResult(res);
    // setActorsResult(res);
  };

  return (
    <div className="relative w-[450px]" aria-hidden="true">
      <div className="flex flex-row gap-2 ">
        <input
          className="h-8 flex items-center  text-start p-2 text-black font-serif text-sm rounded-xl "
          type="text"
          size="50"
          placeholder="Search Movie / Tv Show / Actors "
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyDown={handlerSearch}
        />
        <button
          onClick={handlerSearch}
          className="bg-gray-500 p-1 font-serif font-bold text-sm text-black rounded-xl"
        >
          Search
        </button>
      </div>
      <div className="absolute z-[2] w-[100%] h-[400px] overflow-y-auto bg-gray-300 rounded-xl mt-1 overflow-hidden hidden   ">
        {movieResult?.map((item, index) => {
          return (
            <Link
              to={`/movie/${item.id}`}
              className="flex flex-row gap-10 h-[70px] m-1 border-2 border-black"
              key={index}
            >
              <img
                src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                alt={movieResult.title}
                className=" "
              />
              <div className="flex flex-col justify-between">
                <span className="text-sm font-serif text-black font-bold italic">
                  {item.title}
                </span>
                <span className="text-sm font-serif text-black font-bold italic">
                  {item.release_date}
                </span>
              </div>
            </Link>
          );
        })}
      </div>
    </div>
  );
};

export default SearchBar;
