import React, { useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { fetchData } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { trendingTvShows } from "../../slices/AppSlice";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/scrollbar";

import { Autoplay, Mousewheel, Pagination, Scrollbar } from "swiper/modules";

const CarouselComponentTvNow = ({}) => {
  const dispatch = useDispatch();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchData("trending/tv/day?language=en-US");
      dispatch(trendingTvShows(res));
    }
    fetchDataFunction();
  }, []);
  const trendingTvShow = useSelector((state) => state.app.trendingTv.data);

  return (
    <>
      <Swiper
        direction={"horizontal"}
        slidesPerView={6}
        spaceBetween={0}
        // mousewheel={true}
        // pagination={{
        //   clickable: true,
        // }}
        autoplay={{
          delay: 3000,
          disableOnInteraction: false,
        }}
        scrollbar={{
          hide: true,
        }}
        modules={[Mousewheel, Pagination, Scrollbar, Autoplay]}
      >
        {trendingTvShow?.map((item, index) => {
          return (
            <SwiperSlide>
              <div className="flex flex-col p-2">
                <div key={index}>
                  <img
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                    className="h-auto rounded-xl"
                  />
                  <span className="text-lg font-bold rounded-2xl bg-yellow-400 p-2 text-black">
                    {item.vote_average.toFixed(1)}
                  </span>
                </div>
                <div className="flex flex-col items-start gap-5 h-[30%] justify-between p-2">
                  <h2 className="italic font-bold text-lg">{item.name}</h2>
                  <p className="text-xs text-gray-500">{item.first_air_date}</p>
                </div>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </>
  );
};

export default CarouselComponentTvNow;
