import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchDetails } from "../../utils/utils";
import { setMovieCast, setMoviesDetails } from "../../slices/AppSlice";
import { Link, useParams } from "react-router-dom";
import MovieHeader from "./MovieHeader";

const FullCast = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const res = await fetchDetails(`/movie/${id}/credits?language=en-US`);
      dispatch(setMovieCast(res));
    }
    fetchDataFunction();
  }, [id]);

  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchDetails(`movie/${id}?language=en-US`);
      dispatch(setMoviesDetails(result));
    }

    fetchDataFunction();
  }, [id]);

  const fullMovieCast = useSelector((state) => state.app.moviesActors.data);

  return (
    <div className="flex gap-5 flex-col pl-10 pr-10">
      <MovieHeader />

      {fullMovieCast && (
        <div>
          <div className="grid grid-cols-2 gap-5 ">
            <div className="flex flex-col gap-3 items-center">
              <span className="font-bold underline text-xl italic font-serif">
                Cast
              </span>
              <div className="flex flex-col gap-2">
                {fullMovieCast.cast?.map((item, index) => {
                  return (
                    <div className="flex flex-row gap-2">
                      <div key={index}>
                        <img
                          src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                          alt={item.name}
                          className="w-[100px] h-auto"
                        />
                      </div>
                      <div className="flex flex-col">
                        <span className="text-bold text-xl">{item.name}</span>
                        <span className="text-sm text-gray-500">
                          {item.character}
                        </span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
            <div className="flex flex-col gap-3 items-center">
              <span className="font-bold font-serif text-xl italic underline">
                Crew
              </span>
              <div className="flex flex-col gap-2">
                {fullMovieCast.crew?.map((item, item_index) => {
                  return (
                    <div className="flex flex-row gap-2">
                      <div key={item_index}>
                        <img
                          src={`https://image.tmdb.org/t/p/original${item.profile_path}`}
                          alt={item.name}
                          className="w-[100px] h-auto"
                        />
                      </div>
                      <div className="flex flex-col gap-2">
                        <span className="text-bold text-xl">{item.name}</span>
                        <span className="text-sm text-gray-500">
                          {item.known_for_department}
                        </span>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FullCast;
