import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  tvShow: {
    data: [],
  },
  tvDetail: {
    data: [],
  },
  tvTrailer: {
    data: null,
  },
  tvActors: {
    data: [],
  },
  tvReviews: {
    data: [],
  },
  tvKeyword: {
    data: [],
  },
};

export const TvShowSlice = createSlice({
  name: "series",
  initialState,
  reducers: {
    setTvShow: (state, action) => {
      state.tvShow.data = action.payload;
    },
  },
});

export const { setTvShow } = TvShowSlice.actions;

export default TvShowSlice.reducer;
