import React from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
import Production from "./Production";

const Career = () => {
  const actorData = useSelector((state) => state.actors.actorCredit.data);
  const sortedList = [...actorData.cast];

  return (
    <div>
      <span className="text-3xl italic font-bold text-yellow-500">Acting</span>
      <table>
        <thead className=" mb-3 font-bold italic text-lg bg-yellow-500 text-black rounded-xl justify-items-center">
          <tr>
            <td>Year</td>
            <td>Name</td>
            <td>Role</td>
            <td>Movie/TvShow</td>
          </tr>
        </thead>
        <tbody>
          {actorData.media_type === "movie" ? (
            <div>
              {sortedList
                ?.sort((a, b) => (a.release_date > b.release_date ? 1 : -1))
                .map((item, index) => {
                  return (
                    <tr>
                      <td key={index}>
                        {item.release_date && item.release_date.split("-")[0]}
                      </td>
                      <td>{item.title}</td>
                      <td>{item.media_type === "movie" && item.character}</td>
                      <td>{item.media_type === "movie" && "Movie"}</td>
                    </tr>
                  );
                })}
            </div>
          ) : (
            <div>
              {sortedList
                ?.sort((a, b) => (a.first_air_date > b.first_air_date ? 1 : -1))
                .map((item, index) => {
                  return (
                    <tr>
                      <td key={index}>
                        {item.first_air_date &&
                          item.first_air_date.split("-")[0]}
                      </td>
                      <td>{item.title}</td>
                      <td>{item.media_type === "movie" && item.character}</td>
                      <td>{item.media_type === "movie" && "Movie"}</td>
                    </tr>
                  );
                })}
            </div>
          )}
        </tbody>
      </table>

      {sortedList?.map((item, index) => {
        return (
          <div
            className="grid grid-cols-[15%_45%_20%_20%]  w-[100%] p-1 items-center border-b-2 border-gray-700"
            key={index}
          >
            <div className="flex justify-center items-center">
              {item.release_date
                ? item.release_date.split("-")[0]
                : item.first_air_date
                ? item.first_air_date.split("-")[0]
                : "-"}
            </div>
            <Link to={`/movie/${item.id}`}>
              <div className="hover:text-gray-500">
                {item.title ? item.title : item.name ? item.name : "-"}
              </div>
            </Link>

            <div className="flex justify-center items-center">
              {item.character ? item.character : "-"}
            </div>
            <div className="flex justify-center items-center">
              {item.media_type === "movie" ? "Movie" : "Tv Show"}
            </div>
          </div>
        );
      })}
      <Production />
    </div>
  );
};

export default Career;
