import React, { useEffect } from "react";
import { setTvKeyword } from "../../slices/TvShowSlice";
import { fetchData, fetchDetails } from "../../utils/utils";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";

const Keywords = () => {
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    async function fetchDataFunction() {
      const result = await fetchData(`tv/${id}/keywords`);
      dispatch(setTvKeyword(result));
    }

    fetchDataFunction();
  }, [id]);

  const tvKeywords = useSelector((state) => state.series.tvKeyword.data);

  return (
    <>
      <div className="flex flex-col items-start gap-3">
        <span className="font-serif text-xl italic">Keywords</span>
        {tvKeywords && (
          <ul className="flex items-center pr-5">
            <li className="border rounded-xl p-1 text-sm">{tvKeywords.name}</li>
          </ul>
        )}
      </div>
    </>
  );
};

export default Keywords;
